<?phpsession_start();/* Un identifiant unique, comme : 4b3403665fea6 */printf("uniqid(): %s\r\n", uniqid());echo "<br />";/* Nous pouvez également préfixé l'identifiant unique, * ce qui revient à : * * $uniqid = $prefix . uniqid(); * $uniqid = uniqid($prefix); */printf("uniqid('php_'): %s\r\n", uniqid('php_'));echo "<br />";/* Nous pouvons aussi activer le param�tre more_entropy, * requis par quelques systèmes, comme Cygwin. Ceci fera que * uniqid() produira une valeur comme : 4b340550242239.64159797 */printf("uniqid('', true): %s\r\n", uniqid('', true));echo "<br />";echo session_id();echo "<br />";echo session_save_path();?>